
public class Ques1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 2;
		System.out.println("Table of " + num);
		for(int i = 1; i<= 10; i++) {
			int result = num * i;
			System.out.println("Table of " + num + " * " + i + " = " + result );
		}
	}

}
